import { useState } from "react";
import { useNavigate } from "react-router-dom";
import { v4 as uuidv4, validate } from 'uuid';
import { Toaster, toast } from 'react-hot-toast';
import './JoinRoom.css';

export default function JoinRoom() {
    const navigate = useNavigate();
    const [roomId, setRoomId] = useState("");
    const [username, setUsername] = useState("");

    /**
     * This function now ONLY creates a new Room ID and prepares the form.
     * It does not navigate.
     */
    const handleCreateRoom = (e) => {
        e.preventDefault();
        
        // 1. Generate a new room ID
        const newRoomId = uuidv4();

        // 2. Update the Room ID input field on the screen
        setRoomId(newRoomId);
        
        toast.success("New room created! Enter your username to join.");

        // 3. Focus the username input for the next step
        document.getElementById('usernameInput')?.focus();
    };

    /**
     * This function is now the only way to enter a room.
     * It handles both joining an existing room and a newly created one.
     */
    const handleJoinRoom = (e) => {
        e.preventDefault();
        
        // 1. Validate the Room ID
        if (!validate(roomId.trim())) {
            toast.error("Invalid or missing Room ID.");
            return;
        }

        // 2. Validate the username
        if (!username.trim() || username.trim().length < 2) {
            toast.error("Please enter a valid username (at least 2 characters).");
            return;
        }

        // 3. Save username for page refreshes
        sessionStorage.setItem('username', username.trim());
        
        // 4. Navigate to the room
        navigate(`/room/${roomId.trim()}`, {
            state: { username: username.trim() },
        });
    };

    return (
        <div className="homePageWrapper">
            <header className="header">
                <h1>&lt;&gt; CodeSync</h1>
                <p>Real-time collaborative code editing platform</p>
            </header>

            <section className="featuresGrid">
                <div className="featureCard">
                    <h3>Collaborate</h3>
                    <p>Edit code together in real-time with multiple users.</p>
                </div>
                <div className="featureCard">
                    <h3>Live Chat</h3>
                    <p>Communicate with your team while coding.</p>
                </div>
                <div className="featureCard">
                    <h3>Execute Code</h3>
                    <p>Run code in multiple languages instantly.</p>
                </div>
            </section>
            
            <section className="roomActions">
                <div className="actionBox">
                    <h2>Create New Room</h2>
                    <p>Start a new collaboration session and share the room key with your team.</p>
                    <button className="btn primaryBtn" onClick={handleCreateRoom}>
                        &lt;&gt; Create Room
                    </button>
                </div>
                
                <form className="actionBox" onSubmit={handleJoinRoom}>
                    <h2>Join Existing Room</h2>
                    <p>Enter a room key to join an existing collaboration session.</p>
                    <input
                        type="text"
                        className="inputBox"
                        placeholder="Enter room key..."
                        value={roomId}
                        onChange={(e) => setRoomId(e.target.value)}
                    />
                    <button type="submit" className="btn secondaryBtn">
                        Join Room
                    </button>
                </form>
            </section>
            
            <div style={{marginTop: '2rem', width: '100%', maxWidth: '430px'}}>
                <input
                    id="usernameInput" // ID for focusing
                    type="text"
                    className="inputBox"
                    placeholder="Enter your username here..."
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                />
            </div>

            <Toaster position="top-center" />
        </div>
    );
}
