import { useState } from "react";
import { useNavigate } from "react-router-dom";
import { v4 as uuidv4, validate } from 'uuid';
import { Toaster, toast } from 'react-hot-toast';
import './JoinRoom.css';

export default function JoinRoom() {
    const navigate = useNavigate();
    const [roomId, setRoomId] = useState("");
    const [username, setUsername] = useState("");

    const createNewRoom = (e) => {
        e.preventDefault();
        if (!username.trim() || username.trim().length < 2) {
            toast.error("Please enter a username (at least 2 characters).");
            return;
        }
        const newRoomId = uuidv4();
        sessionStorage.setItem('username', username.trim());
        toast.success("New room created!");
        navigate(`/room/${newRoomId}`, {
            state: { username: username.trim() },
        });
    };

    const joinExistingRoom = (e) => {
        e.preventDefault();
        if (!validate(roomId.trim())) {
            toast.error("Invalid Room ID format.");
            return;
        }
        if (!username.trim() || username.trim().length < 2) {
            toast.error("Please enter a username (at least 2 characters).");
            return;
        }
        sessionStorage.setItem('username', username.trim());
        navigate(`/room/${roomId.trim()}`, {
            state: { username: username.trim() },
        });
    };

    return (
        <div className="homePageWrapper">
            <form className="formCard" onSubmit={joinExistingRoom}>
                <div className="formHeader">
                    <div className="logo">
                        <span>&lt;/&gt;</span>
                    </div>
                    <h2>CodeSync</h2>
                    <p>Enter a Room ID or create a new one.</p>
                </div>
                
                <div className="inputGroup">
                    <input
                        type="text"
                        className="inputBox"
                        placeholder="Enter Room ID (optional)"
                        value={roomId}
                        onChange={(e) => setRoomId(e.target.value)}
                    />
                    <input
                        type="text"
                        className="inputBox"
                        placeholder="Enter your username"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                    />
                </div>

                <div className="btnGroup">
                    <button type="submit" className="btn secondaryBtn">
                        Join Room
                    </button>
                    <button type="button" className="btn primaryBtn" onClick={createNewRoom}>
                        Create New Room
                    </button>
                </div>
            </form>
            <Toaster position="top-center" />
        </div>
    );
}
