import { useState } from "react";
import { useNavigate } from "react-router-dom";
import { v4 as uuidv4, validate } from 'uuid';
import { Toaster, toast } from 'react-hot-toast';
import { FaCode, FaUsers, FaRocket } from 'react-icons/fa'; // Example icons
import './JoinRoom.css';

export default function JoinRoom() {
const navigate = useNavigate();
const [roomId, setRoomId] = useState("");
const [username, setUsername] = useState("");

const handleCreateAndJoin = () => {
    if (!username.trim() || username.trim().length < 2) {
        toast.error("Please enter a username (at least 2 characters).");
        return;
    }
    const newRoomId = uuidv4();
    sessionStorage.setItem('username', username.trim());
    toast.success("New room created!");
    navigate(`/room/${newRoomId}`, { state: { username: username.trim() } });
};

const handleJoinRoom = () => {
    if (!validate(roomId.trim())) {
        toast.error("Invalid Room ID format.");
        return;
    }
    if (!username.trim() || username.trim().length < 2) {
        toast.error("Please enter a username (at least 2 characters).");
        return;
    }
    sessionStorage.setItem('username', username.trim());
    navigate(`/room/${roomId.trim()}`, { state: { username: username.trim() } });
};

return (
    <div className="homePageWrapper">
        <div className="bg-animation">
            <div className="bg-circle"></div>
            <div className="bg-circle"></div>
            <div className="bg-circle"></div>
            <div className="bg-circle"></div>
        </div>

        <section className="hero">
            <h1>&lt;/&gt; CodeSync</h1>
            <p>Real-time collaborative code editing platform</p>
        </section>

        <section className="features">
            <div className="feature-item">
                <div className="feature-icon"><FaCode /></div>
                <div className="feature-title">Collaborate</div>
                <div className="feature-description">Edit code together in real-time.</div>
            </div>
            <div className="feature-item">
                <div className="feature-icon"><FaUsers /></div>
                <div className="feature-title">Live Chat</div>
                <div className="feature-description">Communicate seamlessly while coding.</div>
            </div>
            <div className="feature-item">
                <div className="feature-icon"><FaRocket /></div>
                <div className="feature-title">Execute Code</div>
                <div className="feature-description">Run code in multiple languages instantly.</div>
            </div>
        </section>

        <section className="room-actions">
            <h2 className="action-title">Join or Create a Room</h2>
            <div className="input-group">
                <input
                    type="text"
                    className="inputBox"
                    placeholder="Enter Room ID (optional)"
                    value={roomId}
                    onChange={(e) => setRoomId(e.target.value)}
                />
                <input
                    type="text"
                    className="inputBox"
                    placeholder="Enter your username"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                />
            </div>
            <div className="button-group">
                <button className="btn join-btn" onClick={handleJoinRoom}>Join Room</button>
                <button className="btn create-btn" onClick={handleCreateAndJoin}>Create New & Join</button>
            </div>
        </section>

        <Toaster position="top-center" />
    </div>
);
}
