import { useEffect, useState, useCallback, useRef } from "react";
import AceEditor from "react-ace";
import { Toaster, toast } from 'react-hot-toast';
import { useNavigate, useParams, useLocation } from "react-router-dom";
import { generateColor } from "../../utils";
import './Room.css'

import "ace-builds/src-noconflict/mode-javascript";
import "ace-builds/src-noconflict/mode-typescript";
import "ace-builds/src-noconflict/mode-python";
import "ace-builds/src-noconflict/mode-java";
import "ace-builds/src-noconflict/mode-yaml";
import "ace-builds/src-noconflict/mode-golang";
import "ace-builds/src-noconflict/mode-c_cpp";
import "ace-builds/src-noconflict/mode-html";
import "ace-builds/src-noconflict/mode-css";

import "ace-builds/src-noconflict/keybinding-emacs";
import "ace-builds/src-noconflict/keybinding-vim";

import "ace-builds/src-noconflict/theme-monokai";
import "ace-builds/src-noconflict/ext-language_tools";
import "ace-builds/src-noconflict/ext-searchbox";

export default function Room({ socket, isConnected, username }) {
  const navigate = useNavigate()
  const location = useLocation()
  const { roomId } = useParams()
  const [fetchedUsers, setFetchedUsers] = useState([])
  const [fetchedCode, setFetchedCode] = useState("")
  const [language, setLanguage] = useState("javascript")
  const [codeKeybinding, setCodeKeybinding] = useState(undefined)
  const [isLoading, setIsLoading] = useState(true)
  const [connectionError, setConnectionError] = useState(false)
  const isInitializedRef = useRef(false)

  const languagesAvailable = ["javascript", "java", "c_cpp", "python", "typescript", "golang", "yaml", "html", "css"]
  const codeKeybindingsAvailable = ["default", "emacs", "vim"]

  const currentUsername = username || location.state?.username

  const onChange = useCallback((newValue) => {
    if (!socket || !isConnected) return
    
    setFetchedCode(newValue)
    socket.emit("update code", { roomId, code: newValue })
  }, [socket, roomId, isConnected])

  const handleLanguageChange = useCallback((e) => {
    if (!socket || !isConnected) return
    
    const newLanguage = e.target.value
    setLanguage(newLanguage)
    socket.emit("update language", { roomId, languageUsed: newLanguage })
  }, [socket, roomId, isConnected])

  const handleCodeKeybindingChange = useCallback((e) => {
    const value = e.target.value
    setCodeKeybinding(value === "default" ? undefined : value)
  }, [])

  const handleLeave = useCallback(() => {
    if (!socket || !roomId) {
      navigate('/', { replace: true, state: {} })
      return
    }
    
    try {
      console.log("Leaving room:", roomId)
      socket.emit("leave room", { roomId })
      
      // Small delay to ensure the leave event is processed
      setTimeout(() => {
        navigate('/', { replace: true, state: {} })
      }, 100)
      
    } catch (error) {
      console.error("Error leaving room:", error)
      navigate('/', { replace: true, state: {} })
    }
  }, [socket, roomId, navigate])

  const copyToClipboard = useCallback(async (text) => {
    try {
      if (navigator.clipboard && window.isSecureContext) {
        await navigator.clipboard.writeText(text)
        toast.success('Room ID copied to clipboard')
      } else {
        // Fallback for older browsers or non-HTTPS
        const textArea = document.createElement('textarea')
        textArea.value = text
        textArea.style.position = 'fixed'
        textArea.style.left = '-999999px'
        textArea.style.top = '-999999px'
        document.body.appendChild(textArea)
        textArea.focus()
        textArea.select()
        const successful = document.execCommand('copy')
        textArea.remove()
        if (successful) {
          toast.success('Room ID copied to clipboard')
        } else {
          throw new Error('Copy command failed')
        }
      }
    } catch (error) {
      console.error('Failed to copy room ID:', error)
      toast.error('Failed to copy room ID')
    }
  }, [])

  // Initial setup and sync when room is ready
  useEffect(() => {
    if (!socket || !isConnected || isInitializedRef.current) return

    console.log("Initializing room, requesting sync...")
    
    // Request current state from server
    socket.emit("syncing the code", { roomId })
    socket.emit("syncing the language", { roomId })
    
    isInitializedRef.current = true
    
    // Set loading to false after a brief moment if no data comes
    const loadingTimeout = setTimeout(() => {
      setIsLoading(false)
    }, 2000)

    return () => clearTimeout(loadingTimeout)
  }, [socket, isConnected, roomId])

  // Setup socket event listeners
  useEffect(() => {
    if (!socket) return

    const handleUpdateClientList = ({ userslist }) => {
      console.log("Updated users list:", userslist)
      setFetchedUsers(Array.isArray(userslist) ? userslist : [])
      setIsLoading(false)
      setConnectionError(false)
    }

    const handleLanguageChange = ({ languageUsed }) => {
      console.log("Language changed to:", languageUsed)
      if (languageUsed && languageUsed !== language) {
        setLanguage(languageUsed)
      }
    }

    const handleCodeChange = ({ code }) => {
      console.log("Code updated, length:", code ? code.length : 0)
      setFetchedCode(code || "")
    }

    const handleNewMemberJoined = ({ username }) => {
      console.log(`New member joined: ${username}`)
      toast.success(`${username} joined the room`)
    }

    const handleMemberLeft = ({ username }) => {
      console.log(`Member left: ${username}`)
      toast(`${username} left the room`, {
        icon: 'üëã',
        style: {
          background: '#363636',
          color: '#fff',
        }
      })
    }

    const handleSocketError = (error) => {
      console.error("Socket error:", error)
      setConnectionError(true)
      toast.error(error.message || "Connection error occurred")
    }

    const handleDisconnect = (reason) => {
      console.log("Socket disconnected:", reason)
      setConnectionError(true)
      setIsLoading(false)
      
      if (reason === 'io server disconnect') {
        toast.error("Server disconnected. Trying to reconnect...")
      } else {
        toast.error("Disconnected from server")
      }
    }

    const handleConnect = () => {
      console.log("Socket reconnected")
      setConnectionError(false)
      toast.success("Reconnected to server")
      
      // Re-sync when reconnected
      if (roomId && currentUsername) {
        setTimeout(() => {
          socket.emit("when a user joins", { 
            roomId, 
            username: currentUsername 
          })
        }, 100)
      }
    }

    const handleConnectError = (error) => {
      console.error("Connection error:", error)
      setConnectionError(true)
      setIsLoading(false)
    }

    // Add event listeners
    socket.on("updating client list", handleUpdateClientList)
    socket.on("on language change", handleLanguageChange)
    socket.on("on code change", handleCodeChange)
    socket.on("new member joined", handleNewMemberJoined)
    socket.on("member left", handleMemberLeft)
    socket.on("error", handleSocketError)
    socket.on("disconnect", handleDisconnect)
    socket.on("connect", handleConnect)
    socket.on("connect_error", handleConnectError)

    // Cleanup function to remove event listeners
    return () => {
      socket.off("updating client list", handleUpdateClientList)
      socket.off("on language change", handleLanguageChange)
      socket.off("on code change", handleCodeChange)
      socket.off("new member joined", handleNewMemberJoined)
      socket.off("member left", handleMemberLeft)
      socket.off("error", handleSocketError)
      socket.off("disconnect", handleDisconnect)
      socket.off("connect", handleConnect)
      socket.off("connect_error", handleConnectError)
    }
  }, [socket, language, roomId, currentUsername])

  // Handle browser back button and page refresh
  useEffect(() => {
    const handlePopState = (e) => {
      handleLeave()
    }

    const handleBeforeUnload = (e) => {
      if (socket && roomId) {
        socket.emit("leave room", { roomId })
      }
    }

    window.addEventListener("popstate", handlePopState)
    window.addEventListener("beforeunload", handleBeforeUnload)

    return () => {
      window.removeEventListener("popstate", handlePopState)
      window.removeEventListener("beforeunload", handleBeforeUnload)
    }
  }, [handleLeave, socket, roomId])

  // Redirect if no username
  useEffect(() => {
    if (!currentUsername) {
      console.log("No username found, redirecting to home")
      navigate('/', { replace: true })
    }
  }, [currentUsername, navigate])

  if (!socket) {
    return (
      <div className="room">
        <div className="roomSidebar">
          <div className="connectionStatus error">
            <p>‚ùå Connection Error</p>
            <p>Unable to establish connection to the server.</p>
            <button className="roomSidebarBtn" onClick={() => navigate('/')}>
              Go Back
            </button>
          </div>
        </div>
      </div>
    )
  }

  if (!isConnected) {
    return (
      <div className="room">
        <div className="roomSidebar">
          <div className="connectionStatus">
            <p>üîÑ Connecting...</p>
            <p>Please wait while we connect to the server.</p>
          </div>
        </div>
      </div>
    )
  }

  return (
    <div className="room">
      <div className="roomSidebar">
        <div className="roomSidebarHeader">
          <h3>Room: {roomId ? roomId.substring(0, 8) + '...' : 'Unknown'}</h3>
          {currentUsername && (
            <p className="currentUser">üë§ {currentUsername}</p>
          )}
          {connectionError && (
            <div className="connectionStatus error">
              <p>‚ö†Ô∏è Connection Issues</p>
            </div>
          )}
        </div>

        <div className="roomSidebarUsersWrapper">
          <div className="languageFieldWrapper">
            <label htmlFor="language" className="fieldLabel">Language:</label>
            <select 
              className="languageField" 
              name="language" 
              id="language" 
              value={language} 
              onChange={handleLanguageChange}
              disabled={isLoading || !isConnected}
            >
              {languagesAvailable.map(eachLanguage => (
                <option key={eachLanguage} value={eachLanguage}>
                  {eachLanguage}
                </option>
              ))}
            </select>
          </div>

          <div className="languageFieldWrapper">
            <label htmlFor="codeKeybinding" className="fieldLabel">Keybinding:</label>
            <select 
              className="languageField" 
              name="codeKeybinding" 
              id="codeKeybinding" 
              value={codeKeybinding || "default"} 
              onChange={handleCodeKeybindingChange}
            >
              {codeKeybindingsAvailable.map(eachKeybinding => (
                <option key={eachKeybinding} value={eachKeybinding}>
                  {eachKeybinding}
                </option>
              ))}
            </select>
          </div>

          <div className="usersSection">
            <p className="usersTitle">
              Connected Users {isLoading ? "(Loading...)" : `(${fetchedUsers.length})`}
            </p>
            <div className="roomSidebarUsers">
              {isLoading ? (
                <div className="roomSidebarUsersEach loading">
                  <div className="roomSidebarUsersEachName">üîÑ Loading users...</div>
                </div>
              ) : fetchedUsers.length > 0 ? (
                fetchedUsers.map((each, index) => (
                  <div key={`${each}-${index}`} className="roomSidebarUsersEach">
                    <div 
                      className="roomSidebarUsersEachAvatar" 
                      style={{ backgroundColor: generateColor(each) }}
                    >
                      {each.slice(0, 2).toUpperCase()}
                    </div>
                    <div className="roomSidebarUsersEachName">
                      {each}
                      {each === currentUsername && <span className="youLabel"> (You)</span>}
                    </div>
                  </div>
                ))
              ) : (
                <div className="roomSidebarUsersEach empty">
                  <div className="roomSidebarUsersEachName">
                    {connectionError ? "‚ùå Connection error" : "üë• No users visible"}
                  </div>
                </div>
              )}
            </div>
          </div>
        </div>

        <div className="roomSidebarActions">
          <button 
            className="roomSidebarCopyBtn" 
            onClick={() => copyToClipboard(roomId)}
            disabled={!roomId}
            title="Copy Room ID to share with others"
          >
            üìã Copy Room ID
          </button>
          <button 
            className="roomSidebarBtn" 
            onClick={handleLeave}
            title="Leave this room"
          >
            üö™ Leave Room
          </button>
        </div>
      </div>

      <div className="editorSection">
        {!isConnected && (
          <div className="editorOverlay">
            <p>‚ö†Ô∏è Editor disabled - Connection lost</p>
          </div>
        )}
        <AceEditor
          placeholder={isLoading ? "Loading editor..." : "Write your code here..."}
          className="roomCodeEditor"
          mode={language}
          keyboardHandler={codeKeybinding}
          theme="monokai"
          name="collabEditor"
          width="auto"
          height="auto"
          value={fetchedCode}
          onChange={onChange}
          fontSize={15}
          showPrintMargin={true}
          showGutter={true}
          highlightActiveLine={true}
          enableLiveAutocompletion={true}
          enableBasicAutocompletion={true}
          enableSnippets={true}
          wrapEnabled={true}
          tabSize={2}
          readOnly={isLoading || !isConnected}
          editorProps={{
            $blockScrolling: true
          }}
          setOptions={{
            showLineNumbers: true,
            showFoldWidgets: true,
          }}
        />
      </div>
      
      <Toaster 
        position="top-right"
        toastOptions={{
          duration: 3000,
          style: {
            background: '#363636',
            color: '#fff',
            fontSize: '14px',
          },
          success: {
            iconTheme: {
              primary: '#4aed88',
            },
          },
          error: {
            iconTheme: {
              primary: '#ff6b6b',
            },
          },
        }}
      />
    </div>
  )
}
