import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { v4 as uuidv4, validate } from 'uuid';
import { Toaster, toast } from 'react-hot-toast';
import './JoinRoom.css';

export default function JoinRoom() {
    const navigate = useNavigate();
    const [roomId, setRoomId] = useState("");
    const [username, setUsername] = useState("");
    const [isLoading, setIsLoading] = useState(false);

    useEffect(() => {
        window.history.replaceState(null, '', window.location.pathname);
    }, []);

    function handleRoomSubmit(e) {
        e.preventDefault();
        
        if (!roomId || !roomId.trim()) {
            toast.error("Room ID is required");
            return;
        }
        if (!validate(roomId.trim())) {
            toast.error("Invalid room ID format. Please use a valid UUID.");
            return;
        }
        if (!username || !username.trim()) {
            toast.error("Username is required");
            return;
        }
        const trimmedUsername = username.trim();
        if (trimmedUsername.length < 2 || trimmedUsername.length > 20) {
            toast.error("Username must be between 2 and 20 characters");
            return;
        }

        setIsLoading(true);
        
        try {
            // --- THIS IS THE NEW LINE ---
            // Save the username to session storage to persist it across refreshes
            sessionStorage.setItem('username', trimmedUsername);

            setTimeout(() => {
                navigate(`/room/${roomId.trim()}`, { 
                    state: { 
                        username: trimmedUsername,
                    },
                    replace: true
                });
            }, 100);
        } catch (error) {
            console.error("Navigation error:", error);
            toast.error("Failed to join room");
            setIsLoading(false);
        }
    }

    function createRoomId() {
        if (isLoading) return;
        
        try {
            const newRoomId = uuidv4();
            setRoomId(newRoomId);
            toast.success("Room ID created! Share it with others to collaborate.");
            
            setTimeout(() => {
                document.getElementById('usernameInput')?.focus();
            }, 100);
        } catch (error) {
            console.error("Error creating room:", error);
            toast.error("Failed to create room ID");
        }
    }

    function handleRoomIdChange(e) {
        setRoomId(e.target.value.trim().toLowerCase());
    }

    function handleUsernameChange(e) {
        if (e.target.value.length <= 20) {
            setUsername(e.target.value);
        }
    }

    function handleRoomIdKeyDown(e) {
        if (e.key === 'Enter') {
            e.preventDefault();
            document.getElementById('usernameInput')?.focus();
        }
    }

    return (
        <div className="joinBoxWrapper">
            <form className="joinBox" onSubmit={handleRoomSubmit}>
                <p>Paste your invitation code down below</p>

                <div className="joinBoxInputWrapper">
                    <input
                        className="joinBoxInput"
                        id="roomIdInput"
                        type="text"
                        placeholder="Enter room ID"
                        required
                        onChange={handleRoomIdChange}
                        onKeyDown={handleRoomIdKeyDown}
                        value={roomId}
                        disabled={isLoading}
                    />
                </div>

                <div className="joinBoxInputWrapper">
                    <input
                        className="joinBoxInput"
                        id="usernameInput"
                        type="text"
                        placeholder="Enter Guest Username"
                        required
                        value={username}
                        onChange={handleUsernameChange}
                        disabled={isLoading}
                        maxLength={20}
                    />
                </div>

                <button 
                    className="joinBoxBtn" 
                    type="submit"
                    disabled={isLoading || !roomId || !username || !validate(roomId) || username.trim().length < 2}
                >
                    {isLoading ? "Joining..." : "Join"}
                </button>
                
                <p>
                    Don't have an invite code? Create your{" "}
                    <span
                        style={{ 
                            textDecoration: "underline", 
                            cursor: isLoading ? "not-allowed" : "pointer",
                        }}
                        onClick={isLoading ? undefined : createRoomId}
                    >
                        own room
                    </span>
                </p>
            </form>
            <Toaster position="top-center" />
        </div>
    );
}
