import { useEffect, useState, useCallback, useRef, useMemo } from "react";
import AceEditor from "react-ace";
import { Toaster, toast } from 'react-hot-toast';
import { useNavigate } from "react-router-dom";
import { generateColor } from "../../utils";
import './Room.css';
import { useSocket } from '../../components/SocketWrapper';

// Ace Editor imports
import "ace-builds/src-noconflict/mode-javascript";
import "ace-builds/src-noconflict/mode-typescript";
import "ace-builds/src-noconflict/mode-python";
import "ace-builds/src-noconflict/mode-java";
import "ace-builds/src-noconflict/mode-golang";
import "ace-builds/src-noconflict/mode-c_cpp";
import "ace-builds/src-noconflict/keybinding-emacs";
import "ace-builds/src-noconflict/keybinding-vim";
import "ace-builds/src-noconflict/theme-monokai";
import "ace-builds/src-noconflict/ext-language_tools";
import "ace-builds/src-noconflict/ext-searchbox";

// Enhanced Custom Dropdown Component with better event handling
const CustomDropdown = ({
    label,
    options,
    value,
    onChange,
    id,
    isOpen,
    onToggle,
    disabled = false
}) => {
    const [selectedOption, setSelectedOption] = useState(
        options.find(opt => opt.value === value) || options[0]
    );
    const dropdownRef = useRef(null);
    const buttonRef = useRef(null);
    const menuRef = useRef(null);

    // Handle outside clicks
    useEffect(() => {
        const handleClickOutside = (event) => {
            if (isOpen && dropdownRef.current && !dropdownRef.current.contains(event.target)) {
                onToggle(false);
            }
        };

        const handleEscape = (event) => {
            if (event.key === 'Escape' && isOpen) {
                onToggle(false);
                buttonRef.current?.focus();
            }
        };

        if (isOpen) {
            document.addEventListener('mousedown', handleClickOutside);
            document.addEventListener('keydown', handleEscape);
            // Focus first item when opening
            setTimeout(() => {
                const firstItem = menuRef.current?.querySelector('.custom-dropdown-item');
                firstItem?.focus();
            }, 0);
        }

        return () => {
            document.removeEventListener('mousedown', handleClickOutside);
            document.removeEventListener('keydown', handleEscape);
        };
    }, [isOpen, onToggle]);

    // Update selected option when value changes
    useEffect(() => {
        const newSelectedOption = options.find(opt => opt.value === value) || options[0];
        setSelectedOption(newSelectedOption);
    }, [value, options]);

    const handleSelect = useCallback((option) => {
        setSelectedOption(option);
        onToggle(false);
        onChange(option.value);
        buttonRef.current?.focus();
    }, [onChange, onToggle]);

    const handleKeyDown = useCallback((event, option) => {
        if (event.key === 'Enter' || event.key === ' ') {
            event.preventDefault();
            handleSelect(option);
        }
    }, [handleSelect]);

    const handleButtonKeyDown = useCallback((event) => {
        if (event.key === 'Enter' || event.key === ' ' || event.key === 'ArrowDown') {
            event.preventDefault();
            onToggle(!isOpen);
        }
    }, [isOpen, onToggle]);

    return (
        <div className="languageFieldWrapper">
            <label htmlFor={id} className="fieldLabel">{label}</label>
            <div 
                ref={dropdownRef} 
                className={`custom-dropdown${isOpen ? ' open' : ''}${disabled ? ' disabled' : ''}`}
            >
                <button
                    ref={buttonRef}
                    type="button"
                    className="custom-dropdown-button"
                    onClick={(e) => {
                        e.preventDefault();
                        e.stopPropagation();
                        if (!disabled) {
                            onToggle(!isOpen);
                        }
                    }}
                    onKeyDown={handleButtonKeyDown}
                    aria-expanded={isOpen}
                    aria-haspopup="listbox"
                    aria-labelledby={id}
                    disabled={disabled}
                >
                    <span className="dropdown-text">{selectedOption.label}</span>
                    <svg
                        className={`custom-dropdown-arrow ${isOpen ? 'rotated' : ''}`}
                        width="16"
                        height="16"
                        viewBox="0 0 24 24"
                        fill="none"
                        stroke="currentColor"
                        strokeWidth="2"
                    >
                        <polyline points="6,9 12,15 18,9"></polyline>
                    </svg>
                </button>
                
                {isOpen && !disabled && (
                    <div
                        ref={menuRef}
                        className="custom-dropdown-menu"
                        role="listbox"
                        aria-labelledby={id}
                        onClick={e => e.stopPropagation()}
                    >
                        {options.map((option, index) => (
                            <div
                                key={option.value}
                                className={`custom-dropdown-item ${
                                    selectedOption.value === option.value ? 'selected' : ''
                                }`}
                                onClick={() => handleSelect(option)}
                                onKeyDown={(e) => handleKeyDown(e, option)}
                                role="option"
                                tabIndex={0}
                                aria-selected={selectedOption.value === option.value}
                            >
                                {option.label}
                            </div>
                        ))}
                    </div>
                )}
            </div>
        </div>
    );
};

export default function Room() {
    const { socket, isConnected, username, roomId } = useSocket();
    const navigate = useNavigate();
    const editorRef = useRef(null);

    // Dropdown control - single state to manage which dropdown is open
    const [openDropdownId, setOpenDropdownId] = useState(null);

    // Component states
    const [fetchedUsers, setFetchedUsers] = useState([]);
    const [fetchedCode, setFetchedCode] = useState("");
    const [language, setLanguage] = useState("javascript");
    const [codeKeybinding, setCodeKeybinding] = useState("default");
    const [output, setOutput] = useState("");
    const [isLoading, setIsLoading] = useState(false);
    const [aiResponse, setAiResponse] = useState("");
    const [isAiLoading, setIsAiLoading] = useState(false);
    const [typingUsers, setTypingUsers] = useState([]);
    const typingTimeoutRef = useRef(null);

    // Memoized options to prevent unnecessary re-renders
    const languageOptions = useMemo(() => [
        { value: "javascript", label: "JavaScript" },
        { value: "typescript", label: "TypeScript" },
        { value: "python", label: "Python" },
        { value: "java", label: "Java" },
        { value: "c_cpp", label: "C++" },
        { value: "golang", label: "Go" }
    ], []);

    const keybindingOptions = useMemo(() => [
        { value: "default", label: "Default" },
        { value: "emacs", label: "Emacs" },
        { value: "vim", label: "Vim" }
    ], []);

    // Dropdown handlers
    const handleDropdownToggle = useCallback((dropdownId, isOpen) => {
        setOpenDropdownId(isOpen ? dropdownId : null);
    }, []);

    const closeAllDropdowns = useCallback(() => {
        setOpenDropdownId(null);
    }, []);

    // Close dropdowns on scroll or outside interaction
    useEffect(() => {
        const handleScroll = () => closeAllDropdowns();
        const handleResize = () => closeAllDropdowns();
        
        window.addEventListener('scroll', handleScroll, true);
        window.addEventListener('resize', handleResize);
        
        return () => {
            window.removeEventListener('scroll', handleScroll, true);
            window.removeEventListener('resize', handleResize);
        };
    }, [closeAllDropdowns]);

    // Background effects
    useEffect(() => {
        const createBackgroundEffects = () => {
            // Remove existing background effects
            const existing = document.querySelector('.background-effects');
            if (existing) existing.remove();

            const backgroundDiv = document.createElement('div');
            backgroundDiv.className = 'background-effects';

            for (let i = 0; i < 15; i++) {
                const particle = document.createElement('div');
                particle.className = 'particle';
                particle.style.top = Math.random() * 100 + '%';
                particle.style.left = Math.random() * 100 + '%';
                particle.style.animationDelay = Math.random() * 6 + 's';
                particle.style.animationDuration = (4 + Math.random() * 4) + 's';
                backgroundDiv.appendChild(particle);
            }

            document.body.appendChild(backgroundDiv);
        };

        createBackgroundEffects();

        return () => {
            const backgroundEffects = document.querySelector('.background-effects');
            if (backgroundEffects) {
                backgroundEffects.remove();
            }
        };
    }, []);

    // Code change handler with typing indicators
    const onChange = useCallback((newValue) => {
        if (socket) {
            setFetchedCode(newValue);
            socket.emit("update code", { roomId, code: newValue });
        }
        
        if (socket && username) {
            if (typingTimeoutRef.current) {
                clearTimeout(typingTimeoutRef.current);
            } else {
                socket.emit('typing-start', { roomId, username });
            }
            
            typingTimeoutRef.current = setTimeout(() => {
                socket.emit('typing-stop', { roomId, username });
                typingTimeoutRef.current = null;
            }, 1500);
        }
    }, [socket, roomId, username]);

    // Language change handler
    const handleLanguageChange = useCallback((newLanguage) => {
        setLanguage(newLanguage);
        closeAllDropdowns();
        if (socket) {
            socket.emit("update language", { roomId, languageUsed: newLanguage });
        }
    }, [socket, roomId, closeAllDropdowns]);

    // Keybinding change handler
    const handleCodeKeybindingChange = useCallback((value) => {
        setCodeKeybinding(value);
        closeAllDropdowns();
    }, [closeAllDropdowns]);

    // Leave room handler
    const handleLeave = useCallback(() => {
        if (typingTimeoutRef.current) {
            clearTimeout(typingTimeoutRef.current);
        }
        sessionStorage.removeItem('username');
        if (socket) {
            socket.emit("leave room", { roomId });
        }
        navigate('/', { replace: true, state: {} });
    }, [socket, roomId, navigate]);

    // Copy room ID to clipboard
    const copyToClipboard = useCallback(async () => {
        if (!roomId) return;
        try {
            await navigator.clipboard.writeText(roomId);
            toast.success('Room ID copied to clipboard');
        } catch (error) {
            console.error('Clipboard error:', error);
            toast.error('Failed to copy room ID');
        }
    }, [roomId]);

    // Run code handler
    const handleRunCode = useCallback(async () => {
        if (isLoading) return;
        
        setIsLoading(true);
        setOutput("Executing code...");
        closeAllDropdowns();
        
        try {
            const apiUrl = process.env.REACT_APP_API_URL || 'http://localhost:5000';
            const response = await fetch(`${apiUrl}/execute`, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({
                    language: language,
                    code: fetchedCode
                })
            });
            
            if (!response.ok) {
                throw new Error(`HTTP error! status: ${response.status}`);
            }
            
            const result = await response.json();
            
            if (result.stdout) {
                setOutput(result.stdout);
            } else if (result.stderr) {
                setOutput(result.stderr);
            } else if (result.compile_output) {
                setOutput(result.compile_output);
            } else {
                setOutput("Execution finished with no output.");
            }
        } catch (error) {
            console.error('Code execution error:', error);
            setOutput("An error occurred. Could not run the code.");
        } finally {
            setIsLoading(false);
        }
    }, [isLoading, language, fetchedCode, closeAllDropdowns]);

    // AI request handler
    const handleAiRequest = useCallback(async (prompt) => {
        const editor = editorRef.current?.editor;
        if (!editor) {
            toast.error("Editor not available.");
            return;
        }

        const selectedCode = editor.getSelectedText();
        if (!selectedCode) {
            toast.error("Please select a block of code first.");
            return;
        }

        if (isAiLoading) return;

        setIsAiLoading(true);
        setAiResponse(`AI is thinking about: "${prompt}"...`);
        closeAllDropdowns();

        try {
            const apiUrl = process.env.REACT_APP_API_URL || 'http://localhost:5000';
            const response = await fetch(`${apiUrl}/ask-ai`, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({
                    code: selectedCode,
                    prompt: prompt
                })
            });
            
            if (!response.ok) {
                throw new Error(`HTTP error! status: ${response.status}`);
            }
            
            const result = await response.json();
            setAiResponse(result.response || "No response from AI.");
        } catch (error) {
            console.error('AI request error:', error);
            setAiResponse("An error occurred. Could not get a response from the AI.");
        } finally {
            setIsAiLoading(false);
        }
    }, [isAiLoading, closeAllDropdowns]);

    // Socket event handlers
    useEffect(() => {
        if (!socket) return;

        const handleUpdateClientList = ({ userslist }) => {
            setFetchedUsers(userslist || []);
        };

        const handleLanguageChangeSocket = ({ languageUsed }) => {
            if (languageUsed) {
                setLanguage(languageUsed);
            }
        };

        const handleCodeChange = ({ code }) => {
            setFetchedCode(code || "");
        };

        socket.on("updating client list", handleUpdateClientList);
        socket.on("on language change", handleLanguageChangeSocket);
        socket.on("on code change", handleCodeChange);

        return () => {
            socket.off("updating client list", handleUpdateClientList);
            socket.off("on language change", handleLanguageChangeSocket);
            socket.off("on code change", handleCodeChange);
        };
    }, [socket]);

    // Typing indicators
    useEffect(() => {
        if (!socket) return;

        const handleUserTypingStart = ({ username: typingUsername }) => {
            if (typingUsername && typingUsername !== username) {
                setTypingUsers(prev => [...new Set([...prev, typingUsername])]);
            }
        };

        const handleUserTypingStop = ({ username: typingUsername }) => {
            setTypingUsers(prev => prev.filter(u => u !== typingUsername));
        };

        socket.on('user-typing-start', handleUserTypingStart);
        socket.on('user-typing-stop', handleUserTypingStop);

        return () => {
            socket.off('user-typing-start', handleUserTypingStart);
            socket.off('user-typing-stop', handleUserTypingStop);
        };
    }, [socket, username]);

    // Connection monitoring
    useEffect(() => {
        if (!isConnected || !socket) {
            const timer = setTimeout(() => {
                if (!socket?.connected) {
                    toast.error("Connection lost. Returning to home page.");
                    navigate('/', { replace: true });
                }
            }, 3000);
            return () => clearTimeout(timer);
        }
    }, [isConnected, socket, navigate]);

    // Editor auto-scroll
    useEffect(() => {
        const editor = editorRef.current?.editor;
        if (!editor) return;

        const handleChange = () => {
            const lastLine = editor.session.getLength();
            editor.scrollToLine(lastLine, true, true, () => {});
        };

        editor.on("change", handleChange);
        return () => editor.off("change", handleChange);
    }, []);

    // Cleanup on unmount
    useEffect(() => {
        return () => {
            if (typingTimeoutRef.current) {
                clearTimeout(typingTimeoutRef.current);
            }
        };
    }, []);

    return (
        <div className="room" onClick={closeAllDropdowns}>
            {/* Sidebar */}
            <div className="roomSidebar" onClick={(e) => e.stopPropagation()}>
                <div className="roomSidebarHeader">
                    <div className="room-logo">
                        <div className="logo-icon">&lt;/&gt;</div>
                        <span>CodeSync</span>
                    </div>
                    <h3>Room: {roomId ? roomId.substring(0, 12) + '...' : 'Loading...'}</h3>
                    <p>Collaborative coding session</p>
                </div>
                
                <div className="roomSidebarContent">
                    <CustomDropdown
                        label="Programming Language"
                        options={languageOptions}
                        value={language}
                        onChange={handleLanguageChange}
                        id="language-dropdown"
                        isOpen={openDropdownId === "language"}
                        onToggle={(isOpen) => handleDropdownToggle("language", isOpen)}
                    />

                    <CustomDropdown
                        label="Editor Keybinding"
                        options={keybindingOptions}
                        value={codeKeybinding}
                        onChange={handleCodeKeybindingChange}
                        id="keybinding-dropdown"
                        isOpen={openDropdownId === "keybinding"}
                        onToggle={(isOpen) => handleDropdownToggle("keybinding", isOpen)}
                    />

                    <div className="usersSection">
                        <div className="usersTitle">
                            Active Collaborators ({fetchedUsers.length})
                        </div>
                        <div className="roomSidebarUsers">
                            {fetchedUsers.length === 0 ? (
                                <div className="no-users">No users connected</div>
                            ) : (
                                fetchedUsers.map((user) => (
                                    <div key={user} className="roomSidebarUsersEach">
                                        <div 
                                            className="roomSidebarUsersEachAvatar" 
                                            style={{ 
                                                background: `linear-gradient(135deg, ${generateColor(user)}, ${generateColor(user + 'dark')})` 
                                            }}
                                        >
                                            {user.slice(0, 2).toUpperCase()}
                                        </div>
                                        <div className="roomSidebarUsersEachName">
                                            {user}
                                            {user === username && <span className="youLabel">You</span>}
                                            {typingUsers.includes(user) && user !== username && (
                                                <span className="typing-indicator">typing...</span>
                                            )}
                                        </div>
                                    </div>
                                ))
                            )}
                        </div>
                    </div>
                </div>

                <div className="roomSidebarActions">
                    <button 
                        className="roomSidebarCopyBtn" 
                        onClick={copyToClipboard}
                        disabled={!roomId}
                    >
                        📋 Copy Room ID
                    </button>
                    <button 
                        className="roomSidebarBtn runBtn" 
                        onClick={handleRunCode} 
                        disabled={isLoading || !fetchedCode.trim()}
                    >
                        {isLoading ? '⏳ Running...' : '▶️ Run Code'}
                    </button>
                    <button className="roomSidebarBtn leaveBtn" onClick={handleLeave}>
                        🚪 Leave Room
                    </button>
                </div>
            </div>

            {/* Main Editor Section */}
            <div className="editorSection" onClick={(e) => e.stopPropagation()}>
                <div className="editorControls">
                    <h4>💻 Collaborative Code Editor</h4>
                    <div className="aiControls">
                        <button 
                            className="aiBtn askBtn" 
                            onClick={() => handleAiRequest("Explain this code")} 
                            disabled={isAiLoading}
                        >
                            ✨ Ask AI
                        </button>
                        <button 
                            className="aiBtn bugBtn" 
                            onClick={() => handleAiRequest("Find potential bugs and suggest improvements")} 
                            disabled={isAiLoading}
                        >
                            🐞 Find Bugs
                        </button>
                        <button 
                            className="aiBtn optimizeBtn" 
                            onClick={() => handleAiRequest("Optimize this code")} 
                            disabled={isAiLoading}
                        >
                            🚀 Optimize
                        </button>
                    </div>
                </div>

                <div className="editorWrapper">
                    <AceEditor
                        ref={editorRef}
                        className="roomCodeEditor"
                        mode={language}
                        keyboardHandler={codeKeybinding === "default" ? undefined : codeKeybinding}
                        theme="monokai"
                        name="collabEditor"
                        width="100%"
                        height="100%"
                        value={fetchedCode}
                        onChange={onChange}
                        fontSize={15}
                        enableLiveAutocompletion={true}
                        enableBasicAutocompletion={true}
                        editorProps={{ $blockScrolling: true }}
                        setOptions={{ 
                            showLineNumbers: true, 
                            showPrintMargin: false,
                            highlightActiveLine: true,
                            highlightSelectedWord: true,
                            cursorStyle: "ace",
                            mergeUndoDeltas: false,
                            behavioursEnabled: true,
                            wrapBehavioursEnabled: true,
                            showFoldWidgets: true,
                            foldStyle: "markbegin"
                        }}
                        placeholder="Start typing your code here... Select text and use AI buttons for assistance!"
                    />
                </div>

                {/* Output Panels */}
                <div className="outputContainer">
                    <div className="outputPanel">
                        <h4>💻 Console Output</h4>
                        <pre className="outputBox">
                            {output || "No output yet. Run your code to see results here."}
                        </pre>
                    </div>
                    <div className="aiPanel">
                        <h4>🤖 AI Assistant</h4>
                        <div className="outputBox">
