import { useState, useEffect, useRef } from "react"
import { useNavigate } from "react-router-dom"
import { v4 as uuidv4, validate } from 'uuid';
import { Toaster, toast } from 'react-hot-toast';
import './JoinRoom.css'

export default function JoinRoom() {
    const navigate = useNavigate()
    const [roomId, setRoomId] = useState("")
    const [username, setUsername] = useState("")
    const [isLoading, setIsLoading] = useState(false)
    const usernameRef = useRef(null)

    // Clear any existing navigation state when component mounts
    useEffect(() => {
        window.history.replaceState(null, '', window.location.pathname)
    }, [])

    function handleRoomSubmit(e) {
        e.preventDefault()
        if (isLoading) return; // Prevent double submit

        const cleanedRoomId = roomId.trim().toLowerCase();

        if (!cleanedRoomId) {
            toast.error("Room ID is required")
            return
        }

        if (!validate(cleanedRoomId)) {
            toast.error("Invalid room ID format. Please use a valid UUID.")
            return
        }

        if (!username || !username.trim()) {
            toast.error("Username is required")
            return
        }

        const trimmedUsername = username.trim()

        if (trimmedUsername.length < 2) {
            toast.error("Username must be at least 2 characters long")
            return
        }

        if (trimmedUsername.length > 20) {
            toast.error("Username must be less than 20 characters")
            return
        }

        // Check for invalid characters
        const validUsernamePattern = /^[a-zA-Z0-9\s_-]+$/
        if (!validUsernamePattern.test(trimmedUsername)) {
            toast.error("Username can only contain letters, numbers, spaces, underscores, and hyphens")
            return
        }

        // Prevent duplicate spaces
        if (trimmedUsername.includes('  ')) {
            toast.error("Username cannot contain multiple consecutive spaces")
            return
        }

        setIsLoading(true)

        try {
            setTimeout(() => {
                navigate(`/room/${cleanedRoomId}`, { 
                    state: { 
                        username: trimmedUsername,
                        joinTime: Date.now()
                    },
                    replace: true
                })
            }, 100)
        } catch (error) {
            console.error("Navigation error:", error)
            toast.error("Failed to join room")
            setIsLoading(false)
        }
    }

    function createRoomId() {
        if (isLoading) return
        
        try {
            const newRoomId = uuidv4().toLowerCase()
            setRoomId(newRoomId)
            toast.success("Room ID created! Share it with others to collaborate.", {
                duration: 5000
            })
            setTimeout(() => {
                if (usernameRef.current) {
                    usernameRef.current.focus()
                }
            }, 100)
        } catch (error) {
            console.error("Error creating room:", error)
            toast.error("Failed to create room ID")
        }
    }

    function handleRoomIdChange(e) {
        setRoomId(e.target.value.trim())
    }

    function handleRoomIdPaste(e) {
        e.preventDefault()
        const pastedValue = (e.clipboardData.getData('text') || '').trim().toLowerCase()
        setRoomId(pastedValue)
    }

    function copyRoomIdToClipboard() {
        const cleanedRoomId = roomId.trim().toLowerCase()
        if (cleanedRoomId && validate(cleanedRoomId)) {
            navigator.clipboard.writeText(cleanedRoomId).then(() => {
                toast.success("Room ID copied to clipboard!")
            }).catch(() => {
                toast.error("Failed to copy Room ID")
            })
        }
    }

    function handleRoomIdKeyDown(e) {
        if (e.key === 'Enter') {
            e.preventDefault()
            if (usernameRef.current) {
                usernameRef.current.focus()
            }
        }
    }

    function handleUsernameChange(e) {
        const value = e.target.value
        if (value.length <= 20) {
            setUsername(value)
        }
    }

    return (
        <div className="joinBoxWrapper">
            <form className="joinBox" onSubmit={handleRoomSubmit}>
                <p>Paste your invitation code down below</p>

                <div className="joinBoxInputWrapper">
                    <input
                        className="joinBoxInput"
                        id="roomIdInput"
                        type="text"
                        placeholder="Enter room ID (UUID format)"
                        required
                        onChange={handleRoomIdChange}
                        onPaste={handleRoomIdPaste}
                        onKeyDown={handleRoomIdKeyDown}
                        value={roomId}
                        autoSave="off"
                        autoComplete="off"
                        autoCapitalize="off"
                        spellCheck="false"
                        disabled={isLoading}
                    />
                    <label htmlFor="roomIdInput" className="joinBoxWarning">
                        {roomId ? (validate(roomId.trim().toLowerCase()) ? '' : 'Invalid UUID format') : "Room ID required"}
                    </label>
                    {roomId && validate(roomId.trim().toLowerCase()) && (
                        <button
                            type="button"
                            onClick={copyRoomIdToClipboard}
                            className="copyBtn"
                            style={{
                                fontSize: '12px',
                                padding: '4px 8px',
                                marginTop: '4px',
                                background: 'transparent',
                                border: '1px solid #ccc',
                                borderRadius: '4px',
                                cursor: 'pointer'
                            }}
                            disabled={isLoading}
                        >
                            📋 Copy
                        </button>
                    )}
                </div>

                <div className="joinBoxInputWrapper">
                    <input
                        className="joinBoxInput"
                        id="usernameInput"
                        type="text"
                        placeholder="Enter Guest Username (2-20 characters)"
                        required
                        value={username}
                        onChange={handleUsernameChange}
                        autoSave="off"
                        autoComplete="username"
                        disabled={isLoading}
                        maxLength={20}
                        ref={usernameRef}
                    />
                    <label htmlFor="usernameInput" className="joinBoxWarning">
                        {username ? 
                            (username.trim().length < 2 ? 'At least 2 characters required' : 
                             username.trim().length > 20 ? 'Maximum 20 characters' : '') 
                            : "Username required"}
                    </label>
                </div>

                <button 
                    className="joinBoxBtn" 
                    type="submit"
                    disabled={isLoading || !roomId || !username || !validate(roomId.trim().toLowerCase()) || username.trim().length < 2}
                >
                    {isLoading ? "Joining..." : "Join"}
                </button>
                
                <p>
                    Don't have an invite code? Create your{" "}
                    <span
                        style={{ 
                            textDecoration: "underline", 
                            cursor: isLoading ? "not-allowed" : "pointer",
                            color: isLoading ? "#ccc" : "#007bff",
                            fontWeight: "500"
                        }}
                        onClick={isLoading ? undefined : createRoomId}
                    >
                        own room
                    </span>
                </p>

                {roomId && validate(roomId.trim().toLowerCase()) && (
                    <div style={{ 
                        marginTop: '15px', 
                        padding: '10px', 
                        backgroundColor: '#f8f9fa', 
                        borderRadius: '6px',
                        fontSize: '14px',
                        color: '#666'
                    }}>
                        <strong>Share this Room ID:</strong><br />
                        <code style={{ 
                            backgroundColor: '#e9ecef', 
                            padding: '2px 6px', 
                            borderRadius: '3px',
                            fontFamily: 'monospace'
                        }}>
                            {roomId.trim().toLowerCase()}
                        </code>
                    </div>
                )}
            </form>
            <Toaster 
                position="top-center"
                toastOptions={{
                    duration: 4000,
                    style: {
                        background: '#363636',
                        color: '#fff',
                        fontSize: '14px'
                    },
                    success: {
                        duration: 3000,
                        iconTheme: {
                            primary: '#4aed88',
                        },
                    },
                    error: {
                        duration: 4000,
                        iconTheme: {
                            primary: '#ff6b6b',
                        },
                    },
                }}
            />
        </div>
    )
}
