import { useState } from "react";
import { useNavigate } from "react-router-dom";
import { v4 as uuidv4, validate } from 'uuid';
import { Toaster, toast } from 'react-hot-toast';
import './JoinRoom.css';

export default function JoinRoom() {
    const navigate = useNavigate();
    const [roomId, setRoomId] = useState("");
    const [username, setUsername] = useState("");

    // Logic for creating a new room
    const createNewRoom = (e) => {
        e.preventDefault();
        if (!username.trim() || username.trim().length < 2) {
            toast.error("Please enter a username (at least 2 characters).");
            return;
        }
        const newRoomId = uuidv4();
        sessionStorage.setItem('username', username.trim());
        toast.success("New room created!");
        navigate(`/room/${newRoomId}`, {
            state: { username: username.trim() },
        });
    };

    // Logic for joining an existing room
    const joinExistingRoom = (e) => {
        e.preventDefault();
        if (!validate(roomId.trim())) {
            toast.error("Invalid Room ID format.");
            return;
        }
        if (!username.trim() || username.trim().length < 2) {
            toast.error("Please enter a username (at least 2 characters).");
            return;
        }
        sessionStorage.setItem('username', username.trim());
        navigate(`/room/${roomId.trim()}`, {
            state: { username: username.trim() },
        });
    };

    return (
        <div className="homePageWrapper">
            <header className="header">
                <h1>&lt;&gt; CodeSync</h1>
                <p>Real-time collaborative code editing platform</p>
            </header>

            <section className="featuresGrid">
                <div className="featureCard">
                    <h3>Collaborate</h3>
                    <p>Edit code together in real-time with multiple users.</p>
                </div>
                <div className="featureCard">
                    <h3>Live Chat</h3>
                    <p>Communicate with your team while coding.</p>
                </div>
                <div className="featureCard">
                    <h3>Execute Code</h3>
                    <p>Run code in multiple languages instantly.</p>
                </div>
            </section>
            
            <section className="roomActions">
                <div className="actionBox">
                    <h2>Create New Room</h2>
                    <p>Start a new collaboration session and share the room key with your team.</p>
                    <button className="btn primaryBtn" onClick={createNewRoom}>
                        &lt;&gt; Create Room
                    </button>
                </div>
                
                <form className="actionBox" onSubmit={joinExistingRoom}>
                    <h2>Join Existing Room</h2>
                    <p>Enter a room key to join an existing collaboration session.</p>
                    <input
                        type="text"
                        className="inputBox"
                        placeholder="Enter room key..."
                        value={roomId}
                        onChange={(e) => setRoomId(e.target.value)}
                    />
                    <button type="submit" className="btn secondaryBtn">
                        Join Room
                    </button>
                </form>
            </section>
            
            <div style={{marginTop: '2rem', width: '100%', maxWidth: '430px'}}>
                <input
                    type="text"
                    className="inputBox"
                    placeholder="Enter your username here..."
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                />
            </div>

            <Toaster position="top-center" />
        </div>
    );
}
