import { useEffect, useState, useCallback, useRef } from "react";
import AceEditor from "react-ace";
import { Toaster, toast } from 'react-hot-toast';
import { useNavigate } from "react-router-dom";
import { generateColor } from "../../utils";
import './Room.css';
import { useSocket } from '../../components/SocketWrapper';

// Import necessary modes, themes, and extensions for Ace Editor
import "ace-builds/src-noconflict/mode-javascript";
import "ace-builds/src-noconflict/mode-typescript";
import "ace-builds/src-noconflict/mode-python";
import "ace-builds/src-noconflict/mode-java";
import "ace-builds/src-noconflict/mode-golang";
import "ace-builds/src-noconflict/mode-c_cpp";
import "ace-builds/src-noconflict/keybinding-emacs";
import "ace-builds/src-noconflict/keybinding-vim";
import "ace-builds/src-noconflict/theme-monokai";
import "ace-builds/src-noconflict/ext-language_tools";
import "ace-builds/src-noconflict/ext-searchbox";

// Custom Dropdown Component
const CustomDropdown = ({ 
    label, 
    options, 
    value, 
    onChange, 
    id
}) => {
    const [isOpen, setIsOpen] = useState(false);
    const [selectedOption, setSelectedOption] = useState(
        options.find(opt => opt.value === value) || options[0]
    );
    const dropdownRef = useRef(null);

    useEffect(() => {
        const handleClickOutside = (event) => {
            if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
                setIsOpen(false);
            }
        };

        document.addEventListener('mousedown', handleClickOutside);
        return () => document.removeEventListener('mousedown', handleClickOutside);
    }, []);

    useEffect(() => {
        const newSelectedOption = options.find(opt => opt.value === value) || options[0];
        setSelectedOption(newSelectedOption);
    }, [value, options]);

    const handleSelect = (option) => {
        setSelectedOption(option);
        setIsOpen(false);
        onChange({ target: { value: option.value } });
    };

    return (
        <div className="languageFieldWrapper">
            <label htmlFor={id} className="fieldLabel">{label}</label>
            <div ref={dropdownRef} className={`custom-dropdown ${isOpen ? 'open' : ''}`}>
                <button
                    type="button"
                    className="custom-dropdown-button"
                    onClick={() => setIsOpen(!isOpen)}
                    aria-expanded={isOpen}
                    aria-haspopup="listbox"
                >
                    <span>{selectedOption.label}</span>
                    <svg 
                        className="custom-dropdown-arrow" 
                        width="16" 
                        height="16" 
                        viewBox="0 0 24 24" 
                        fill="none" 
                        stroke="currentColor" 
                        strokeWidth="2"
                    >
                        <polyline points="6,9 12,15 18,9"></polyline>
                    </svg>
                </button>
                {isOpen && (
                    <div className="custom-dropdown-menu" role="listbox">
                        {options.map((option) => (
                            <div
                                key={option.value}
                                className={`custom-dropdown-item ${
                                    selectedOption.value === option.value ? 'selected' : ''
                                }`}
                                onClick={() => handleSelect(option)}
                                role="option"
                                aria-selected={selectedOption.value === option.value}
                            >
                                {option.label}
                            </div>
                        ))}
                    </div>
                )}
            </div>
        </div>
    );
};

export default function Room() {
    const { socket, isConnected, username, roomId } = useSocket();
    const navigate = useNavigate();
    const editorRef = useRef(null);

    // States
    const [fetchedUsers, setFetchedUsers] = useState([]);
    const [fetchedCode, setFetchedCode] = useState("");
    const [language, setLanguage] = useState("javascript");
    const [codeKeybinding, setCodeKeybinding] = useState(undefined);
    const [output, setOutput] = useState("");
    const [isLoading, setIsLoading] = useState(false);
    const [aiResponse, setAiResponse] = useState("");
    const [isAiLoading, setIsAiLoading] = useState(false);
    const [typingUsers, setTypingUsers] = useState([]);
    const typingTimeoutRef = useRef(null);

    const languagesAvailable = ["javascript", "java", "c_cpp", "python", "typescript", "golang"];
    const codeKeybindingsAvailable = ["default", "emacs", "vim"];

    // Create background particles on component mount
    useEffect(() => {
        const createBackgroundEffects = () => {
            const backgroundDiv = document.createElement('div');
            backgroundDiv.className = 'background-effects';
            
            // Create particles
            for (let i = 0; i < 15; i++) {
                const particle = document.createElement('div');
                particle.className = 'particle';
                particle.style.top = Math.random() * 100 + '%';
                particle.style.left = Math.random() * 100 + '%';
                particle.style.animationDelay = Math.random() * 6 + 's';
                particle.style.animationDuration = (4 + Math.random() * 4) + 's';
                backgroundDiv.appendChild(particle);
            }
            
            document.body.appendChild(backgroundDiv);
        };

        createBackgroundEffects();

        // Cleanup on unmount
        return () => {
            const backgroundEffects = document.querySelector('.background-effects');
            if (backgroundEffects) {
                backgroundEffects.remove();
            }
        };
    }, []);

    const onChange = useCallback((newValue) => {
        if (socket) {
            setFetchedCode(newValue);
            socket.emit("update code", { roomId, code: newValue });
        }
        if (socket && username) {
            if (typingTimeoutRef.current) {
                clearTimeout(typingTimeoutRef.current);
            } else {
                socket.emit('typing-start', { roomId, username });
            }
            typingTimeoutRef.current = setTimeout(() => {
                socket.emit('typing-stop', { roomId, username });
                typingTimeoutRef.current = null;
            }, 1500);
        }
    }, [socket, roomId, username]);

    const handleLanguageChange = useCallback((e) => {
        if (socket) {
            const newLanguage = e.target.value;
            setLanguage(newLanguage);
            socket.emit("update language", { roomId, languageUsed: newLanguage });
        }
    }, [socket, roomId]);

    const handleCodeKeybindingChange = useCallback((e) => {
        const value = e.target.value;
        setCodeKeybinding(value === "default" ? undefined : value);
    }, []);

    const handleLeave = useCallback(() => {
        sessionStorage.removeItem('username');
        if (socket) {
            socket.emit("leave room", { roomId });
        }
        navigate('/', { replace: true, state: {} });
    }, [socket, roomId, navigate]);

    const copyToClipboard = useCallback(async () => {
        if (!roomId) return;
        try {
            await navigator.clipboard.writeText(roomId);
            toast.success('Room ID copied to clipboard');
        } catch (error) {
            toast.error('Failed to copy room ID');
        }
    }, [roomId]);

    const handleRunCode = async () => {
        setIsLoading(true);
        setOutput("Executing code...");
        try {
            const apiUrl = process.env.REACT_APP_API_URL || 'http://localhost:5000';
            const response = await fetch(`${apiUrl}/execute`, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({
                    language: language,
                    code: fetchedCode
                })
            });
            const result = await response.json();
            if (result.stdout) setOutput(result.stdout);
            else if (result.stderr) setOutput(result.stderr);
            else if (result.compile_output) setOutput(result.compile_output);
            else setOutput("Execution finished with no output.");
        } catch (error) {
            setOutput("An error occurred. Could not run the code.");
        } finally {
            setIsLoading(false);
        }
    };

    const handleAiRequest = async (prompt) => {
        const editor = editorRef.current?.editor;
        if (!editor) return;

        const selectedCode = editor.getSelectedText();
        if (!selectedCode) {
            toast.error("Please select a block of code first.");
            return;
        }

        setIsAiLoading(true);
        setAiResponse(`AI is thinking about: "${prompt}"...`);

        try {
            const apiUrl = process.env.REACT_APP_API_URL || 'http://localhost:5000';
            const response = await fetch(`${apiUrl}/ask-ai`, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({
                    code: selectedCode,
                    prompt: prompt
                })
            });
            const result = await response.json();
            setAiResponse(result.response || "No response from AI.");
        } catch (error) {
            setAiResponse("An error occurred. Could not get a response from the AI.");
        } finally {
            setIsAiLoading(false);
        }
    };

    useEffect(() => {
        if (!socket) return;
        const handleUpdateClientList = ({ userslist }) => setFetchedUsers(userslist || []);
        const handleLanguageChange = ({ languageUsed }) => { if (languageUsed) setLanguage(languageUsed); };
        const handleCodeChange = ({ code }) => setFetchedCode(code || "");

        socket.on("updating client list", handleUpdateClientList);
        socket.on("on language change", handleLanguageChange);
        socket.on("on code change", handleCodeChange);

        return () => {
            socket.off("updating client list", handleUpdateClientList);
            socket.off("on language change", handleLanguageChange);
            socket.off("on code change", handleCodeChange);
        };
    }, [socket]);
    
    useEffect(() => {
        if (!socket) return;
        const handleUserTypingStart = ({ username }) => {
            setTypingUsers(prev => [...new Set([...prev, username])]);
        };
        const handleUserTypingStop = ({ username }) => {
            setTypingUsers(prev => prev.filter(u => u !== username));
        };
        socket.on('user-typing-start', handleUserTypingStart);
        socket.on('user-typing-stop', handleUserTypingStop);
        return () => {
            socket.off('user-typing-start', handleUserTypingStart);
            socket.off('user-typing-stop', handleUserTypingStop);
        };
    }, [socket]);

    useEffect(() => {
        if (!isConnected || !socket) {
            const timer = setTimeout(() => {
                if (!socket?.connected) {
                    toast.error("Connection lost. Returning to home page.");
                    navigate('/', {replace: true});
                }
            }, 3000);
            return () => clearTimeout(timer);
        }
    }, [isConnected, socket, navigate]);

    return (
        <div className="room">
            {/* Sidebar with Logo */}
            <div className="roomSidebar">
                <div className="roomSidebarHeader">
                    <div className="room-logo">                        
                        <span>&lt;CodeSync&gt;</span>
                    </div>
                    <h3>Room ID: {roomId ? roomId.substring(0, 12) : '...'}</h3>
                    <p>Collaborative coding session</p>
                </div>
                
                <div className="roomSidebarContent">
                    <CustomDropdown
                        label="Programming Language"
                        options={languagesAvailable.map(lang => ({
                            value: lang,
                            label: lang === 'c_cpp' ? 'C++' : lang.charAt(0).toUpperCase() + lang.slice(1)
                        }))}
                        value={language}
                        onChange={handleLanguageChange}
                        id="language"
                    />

                    <CustomDropdown
                        label="Editor Keybinding"
                        options={codeKeybindingsAvailable.map(kb => ({
                            value: kb,
                            label: kb.charAt(0).toUpperCase() + kb.slice(1)
                        }))}
                        value={codeKeybinding || "default"}
                        onChange={handleCodeKeybindingChange}
                        id="codeKeybinding"
                    />

                    <div className="usersSection">
                        <div className="usersTitle">Active Collaborators ({fetchedUsers.length})</div>
                        <div className="roomSidebarUsers">
                            {fetchedUsers.map((user) => (
                                <div key={user} className="roomSidebarUsersEach">
                                    <div 
                                        className="roomSidebarUsersEachAvatar" 
                                        style={{ background: `linear-gradient(135deg, ${generateColor(user)}, ${generateColor(user + 'dark')})` }}
                                    >
                                        {user.slice(0, 2).toUpperCase()}
                                    </div>
                                    <div className="roomSidebarUsersEachName">
                                        {user}
                                        {user === username && <span className="youLabel">You</span>}
                                        {typingUsers.includes(user) && user !== username && (
                                            <span className="typing-indicator">typing...</span>
                                        )}
                                    </div>
                                </div>
                            ))}
                        </div>
                    </div>
                </div>

                <div className="roomSidebarActions">
                    <button className="roomSidebarCopyBtn" onClick={copyToClipboard}>
                        Copy Room ID
                    </button>
                    <button className="roomSidebarBtn runBtn" onClick={handleRunCode} disabled={isLoading}>
                        {isLoading ? '‚è≥ Running...' : 'Run Code'}
                    </button>
                    <button className="roomSidebarBtn" onClick={handleLeave}>
                        Leave Room
                    </button>
                </div>
            </div>

            {/* Main Editor Section */}
            <div className="editorSection">
                <div className="editorControls">
                    <h4> &lt;CodeSync&gt; </h4>
                    <div>
                        <button 
                            className="aiBtn" 
                            onClick={() => handleAiRequest("Ask AI")} 
                            disabled={isAiLoading}
                        >
                            Ask AI‚ú®
                        </button>
                        <button 
                            className="aiBtn" 
                            onClick={() => handleAiRequest("Find potential bugs")} 
                            disabled={isAiLoading}
                        >
                            Find Bugsüêû
                        </button>
                    </div>
                </div>

                <AceEditor
                    ref={editorRef}
                    className="roomCodeEditor"
                    mode={language}
                    keyboardHandler={codeKeybinding}
                    theme="monokai"
                    name="collabEditor"
                    width="100%"
                    height="100%"
                    value={fetchedCode}
                    onChange={onChange}
                    fontSize={15}
                    enableLiveAutocompletion={true}
                    enableBasicAutocompletion={true}
                    editorProps={{ $blockScrolling: true }}
                    setOptions={{ 
                        showLineNumbers: true, 
                        showPrintMargin: false,
                        highlightActiveLine: true,
                        highlightSelectedWord: true,
                        cursorStyle: "ace",
                        mergeUndoDeltas: false,
                        behavioursEnabled: true,
                        wrapBehavioursEnabled: true
                    }}
                />
            </div>

            {/* Output Panels */}
            <div className="outputContainer">
                <div className="outputPanel">
                    <h4>üíª Console Output</h4>
                    <pre className="outputBox">{output || "No output yet. Run your code to see results here."}</pre>
                </div>
                <div className="aiPanel">
                    <h4>ü§ñ Your AI Assistant</h4>
                    <div className="outputBox">
                        {aiResponse || "Welcome to your collaborative coding session!\n\nI can help you with:\n‚Ä¢Select some code and click an AI button to get started!"}
                    </div>
                </div>
            </div>
            
            <Toaster 
                position="top-right"
                toastOptions={{
                    style: {
                        background: 'rgba(255, 255, 255, 0.1)',
                        backdropFilter: 'blur(20px)',
                        color: '#f0f2f5',
                        border: '1px solid rgba(255, 255, 255, 0.12)',
                        borderRadius: '16px',
                    },
                }}
            />
        </div>
    );
}
