import { useState } from "react"
import { useNavigate } from "react-router-dom"
import { v4 as uuidv4, validate } from 'uuid';
import { Toaster, toast } from 'react-hot-toast';
import './JoinRoom.css'

export default function JoinRoom() {
    const navigate = useNavigate()
    const [roomId, setRoomId] = useState("")
    const [username, setUsername] = useState("")
    const [isLoading, setIsLoading] = useState(false)

    function handleRoomSubmit(e) {
        e.preventDefault()
        
        // Validate room ID
        if (!roomId || !roomId.trim()) {
            toast.error("Room ID is required")
            return
        }
        
        if (!validate(roomId)) {
            toast.error("Invalid room ID format")
            return
        }

        // Validate username
        if (!username || !username.trim()) {
            toast.error("Username is required")
            return
        }

        if (username.trim().length < 2) {
            toast.error("Username must be at least 2 characters long")
            return
        }

        if (username.trim().length > 20) {
            toast.error("Username must be less than 20 characters")
            return
        }

        setIsLoading(true)
        
        try {
            navigate(`/room/${roomId}`, { 
                state: { username: username.trim() } 
            })
        } catch (error) {
            console.error("Navigation error:", error)
            toast.error("Failed to join room")
            setIsLoading(false)
        }
    }

    function createRoomId() {
        try {
            const newRoomId = uuidv4()
            setRoomId(newRoomId)
            toast.success("Room ID created! Share it with others to collaborate.")
        } catch (error) {
            console.error("Error creating room:", error)
            toast.error("Failed to create room ID")
        }
    }

    function handleRoomIdChange(e) {
        const value = e.target.value.trim()
        setRoomId(value)
    }

    function handleUsernameChange(e) {
        const value = e.target.value
        // Allow spaces but limit length
        if (value.length <= 20) {
            setUsername(value)
        }
    }

    return (
        <div className="joinBoxWrapper">
            <form className="joinBox" onSubmit={handleRoomSubmit}>
                <p>Paste your invitation code down below</p>

                <div className="joinBoxInputWrapper">
                    <input
                        className="joinBoxInput"
                        id="roomIdInput"
                        type="text"
                        placeholder="Enter room ID"
                        required
                        onChange={handleRoomIdChange}
                        value={roomId}
                        autoSave="off"
                        autoComplete="off"
                        disabled={isLoading}
                    />
                    <label htmlFor="roomIdInput" className="joinBoxWarning">
                        {roomId ? '' : "Room ID required"}
                    </label>
                </div>

                <div className="joinBoxInputWrapper">
                    <input
                        className="joinBoxInput"
                        id="usernameInput"
                        type="text"
                        placeholder="Enter Guest Username"
                        required
                        value={username}
                        onChange={handleUsernameChange}
                        autoSave="off"
                        autoComplete="off"
                        disabled={isLoading}
                        maxLength={20}
                    />
                    <label htmlFor="usernameInput" className="joinBoxWarning">
                        {username ? '' : "Username required"}
                    </label>
                </div>

                <button 
                    className="joinBoxBtn" 
                    type="submit"
                    disabled={isLoading}
                >
                    {isLoading ? "Joining..." : "Join"}
                </button>
                
                <p>
                    Don't have an invite code? Create your{" "}
                    <span
                        style={{ 
                            textDecoration: "underline", 
                            cursor: "pointer",
                            color: isLoading ? "#ccc" : "inherit"
                        }}
                        onClick={isLoading ? undefined : createRoomId}
                    >
                        own room
                    </span>
                </p>
            </form>
            <Toaster 
                position="top-center"
                toastOptions={{
                    duration: 4000,
                    style: {
                        background: '#363636',
                        color: '#fff',
                    },
                    success: {
                        duration: 3000,
                        iconTheme: {
                            primary: '#4aed88',
                        },
                    },
                    error: {
                        duration: 4000,
                        iconTheme: {
                            primary: '#ff6b6b',
                        },
                    },
                }}
            />
        </div>
    )
}
