import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { v4 as uuidv4, validate } from 'uuid';
import { Toaster, toast } from 'react-hot-toast';
import './JoinRoom.css';

export default function JoinRoom() {
    const navigate = useNavigate();
    const [roomId, setRoomId] = useState("");
    const [username, setUsername] = useState("");
    const [isLoading, setIsLoading] = useState(false);

    useEffect(() => {
        window.history.replaceState(null, '', window.location.pathname);
    }, []);

    function handleRoomSubmit(e) {
        e.preventDefault();
        
        if (!roomId || !roomId.trim()) {
            toast.error("Room ID is required");
            return;
        }
        if (!validate(roomId.trim())) {
            toast.error("Invalid room ID format. Please use a valid UUID.");
            return;
        }
        if (!username || !username.trim()) {
            toast.error("Username is required");
            return;
        }
        const trimmedUsername = username.trim();
        if (trimmedUsername.length < 2 || trimmedUsername.length > 20) {
            toast.error("Username must be between 2 and 20 characters");
            return;
        }

        setIsLoading(true);
        
        try {
            sessionStorage.setItem('username', trimmedUsername);

            setTimeout(() => {
                navigate(`/room/${roomId.trim()}`, { 
                    state: { 
                        username: trimmedUsername,
                    },
                    replace: true
                });
            }, 100);
        } catch (error) {
            console.error("Navigation error:", error);
            toast.error("Failed to join room");
            setIsLoading(false);
        }
    }

    function createRoomId() {
        if (isLoading) return;
        
        // --- THIS IS THE NEW CHECK ---
        // Prevents creating a new room if a room ID is already entered
        if (roomId.trim()) {
            toast.error("You have entered a Room ID. Please click 'Join Room' instead.");
            return;
        }
        
        try {
            const newRoomId = uuidv4();
            setRoomId(newRoomId);
            toast.success("Room ID created! Share it with others to collaborate.");
            
            setTimeout(() => {
                document.getElementById('usernameInput')?.focus();
            }, 100);
        } catch (error) {
            console.error("Error creating room:", error);
            toast.error("Failed to create room ID");
        }
    }

    function handleRoomIdChange(e) {
        setRoomId(e.target.value.trim().toLowerCase());
    }

    function handleUsernameChange(e) {
        if (e.target.value.length <= 20) {
            setUsername(e.target.value);
        }
    }

    function handleRoomIdKeyDown(e) {
        if (e.key === 'Enter') {
            e.preventDefault();
            document.getElementById('usernameInput')?.focus();
        }
    }

    return (
        <div className="homePageWrapper">
            <header className="header">
                <h1>&lt;/&gt; CodeSync</h1>
                <p>Real-time collaborative code editing platform</p>
            </header>

            <section className="featuresGrid">
                <div className="featureCard">
                    <h3>Collaborate</h3>
                    <p>Edit code together in real-time with multiple users.</p>
                </div>
                <div className="featureCard">
                    <h3>Live Chat</h3>
                    <p>Communicate with your team while coding.</p>
                </div>
                <div className="featureCard">
                    <h3>Execute Code</h3>
                    <p>Run code in multiple languages instantly.</p>
                </div>
            </section>
            
            <section className="formContainer">
                <div className="formHeader">
                    <h2>Join or Create a Room</h2>
                </div>
                
                <div className="inputGroup">
                    <input
                        type="text"
                        className="inputBox"
                        placeholder="Enter your username"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                    />
                    <input
                        type="text"
                        className="inputBox"
                        placeholder="Enter Room ID (optional)"
                        value={roomId}
                        onChange={(e) => setRoomId(e.target.value)}
                    />
                </div>
                
                <div className="btnGroup">
                    <button className="btn secondaryBtn" onClick={handleJoinRoom}>
                        Join Room
                    </button>
                    <button className="btn primaryBtn" onClick={handleCreateRoom}>
                        Create Room
                    </button>
                </div>
            </section>

            <Toaster position="top-center" />
        </div>
    );
}
